import { createElement, forwardRef } from 'rax';
import { useLocale } from './hooks';
export default function withLocale(Component, { name, defaultLocale }) {
    return forwardRef((props, ref) => {
        const locale = useLocale(name, props, defaultLocale);
        return createElement(Component, Object.assign({}, props, { locale: locale, ref: ref }));
    });
}
