const path = require('path');
const fs = require('fs-extra');
const cwd = process.cwd();
const buildJSONPath = path.resolve(cwd, 'build.json');
const buildJson = fs.readJSONSync(buildJSONPath);
let multipleProps = {};
(buildJson.plugins || []).forEach(plugin => {
  if (Array.isArray(plugin) && plugin[1] && plugin[0] === 'build-plugin-component-multiple') {
    multipleProps = plugin[1];
  }
});

module.exports = {
  multipleProps
};
