import React from 'react';

export interface IContext {
  lang?: 'zh-CN' | 'en';
  theme?: 'light' | 'dark';
  setTheme?: React.Dispatch<React.SetStateAction<'light' | 'dark'>>;
  setLang?: React.Dispatch<React.SetStateAction<'zh-CN' | 'en'>>;
}

const BaseContext = React.createContext<IContext>({
  lang: 'zh-CN',
  theme: 'light',
});

export default BaseContext;
