const getSingleComponentInfo = require('./getSingleComponentInfo');
const getAllComponentInfo = require('./getAllComponentInfo');

module.exports = {
  getSingleComponentInfo,
  getAllComponentInfo,
  getCurComponentInfo: function (context, multiple, componentName) {
    const { command } = context;

    return !multiple
      ? getSingleComponentInfo(context)
      : getAllComponentInfo(context, {
          multiple,
          component: command === 'start' ? componentName : '',
        });
  },
};
