import * as React from 'react';

export interface ISelectProps {
  /**
   * 传入的数据源，可以动态渲染子项
   */
  dataSource: string[];
  /**
   * Select发生改变时触发的回调
   */
  onChange?: (item: string) => void;
  /**
   * 是否只读，只读模式下可以展开弹层但不能选
   */
  readOnly?: boolean;
  /**
   * 选择器尺寸
   */
  size?: 'small' | 'medium' | 'large';
  /**
   * 当前值，用于受控模式
   */
  value?: string | number;
}

/**
 * 选择器
 */
const Select: React.FC<ISelectProps> = () => {
  return <div>Test</div>;
};

Select.defaultProps = {
  readOnly: false,
  size: 'medium',
};

export default Select;
