import * as React from 'react';

interface SubProps extends React.HTMLAttributes<HTMLDivElement> {
  /**
   * 除该属性外，其余都会被过滤
   */
  a: string;
}
export interface ISelectProps extends React.HTMLAttributes<HTMLDivElement> {
  /**
   * 是否只读，只读模式下可以展开弹层但不能选
   */
  readOnly?: boolean;

  subProps: SubProps;
}

/**
 * 选择器
 */
const Select: React.FC<ISelectProps> = () => {
  return <div>Test</div>;
};

Select.defaultProps = {
  readOnly: false,
};

export default Select;
