import { MaterialPackageJSON } from '../../types';
import { renderBlockPackageJSON } from './renderBlockPackageJSON';
import { renderComponentPackageJSON } from './renderComponentPackageJSON';

export const renderPackageJSON = (
  packageJSON: MaterialPackageJSON,
  source: string
) =>
  packageJSON.blockConfig
    ? renderBlockPackageJSON(packageJSON, source)
    : renderComponentPackageJSON(packageJSON, source);
