import { heading, paragraph, text, link } from 'mdast-builder';
import { MaterialPackageJSON } from '../../types';
import * as qs from 'qs';
import { getGitRemoteUrl } from '../../utils/remoteUrl';
import {
  gitlabBugIssueBadge,
  gitlabEnhancementIssueBadge,
  tnpmDownloadsBadge,
  tnpmVersionBadge,
} from './renderBadges';

export const renderBlockPackageJSON = (
  packageJSON: MaterialPackageJSON,
  source: string
) =>
  [
    heading(1, text(packageJSON.name)),
    paragraph(renderBadges(packageJSON, source)),
    paragraph(text(packageJSON.description)),
    heading(2, text('使用方法')),
    packageJSON.blockConfig &&
      paragraph(
        link(
          'https://yuque.antfin-inc.com/bp-xt-dt/battle/blocks_used_in_iceworks',
          '如何iceworks中使用区块',
          text('如何iceworks中使用区块')
        )
      ),
    heading(2, text('API')),
  ].filter((vo) => vo);

const renderBadges = (packageJSON: MaterialPackageJSON, source: string) => {
  const remoteUrl = getGitRemoteUrl(source);
  const materialConfig = packageJSON.blockConfig;
  return [
    tnpmVersionBadge(packageJSON.name, packageJSON.version),
    text('\n'),
    tnpmDownloadsBadge(packageJSON.name),
    text('\n'),
    remoteUrl &&
      gitlabBugIssueBadge(
        `${remoteUrl}/issues/new?${qs.stringify({
          issue: {
            title: `[Bug]${materialConfig?.name || packageJSON.name}: `,
            description: [
              '### 环境信息',
              '',
              `- 区块名称： ${materialConfig?.title}`,
              `- 区块版本： ${packageJSON.version}`,
              '',
              '<!-- 在此描述Bug -->',
              '',
              '### 当前行为',
              '',
              '### 期望行为',
              '',
              '### 复现方式',
              '',
            ].join('\n'),
            issuable_template: 'bug',
          },
        })}`,
        '提Bug'
      ),
    remoteUrl && text('\n'),
    remoteUrl &&
      gitlabEnhancementIssueBadge(
        `${remoteUrl}/issues/new?${qs.stringify({
          issue: {
            title: `[需求]${materialConfig?.name || packageJSON.name}: `,
            description: [
              '### 环境信息',
              '',
              `- 区块名称: ${materialConfig?.name} ${materialConfig?.title}`,
              `- 区块版本: ${packageJSON.version}`,
              '',
              '<!-- 在此描述需求 -->',
              '',
              '### 期望行为',
              '',
              '### UI截图(如果有设计稿请附上)',
              '',
            ].join('\n'),
            issuable_template: 'enhancement',
          },
        })}`,
        '提需求'
      ),
  ].filter((vo) => vo);
};
