/**
 * 物料的package.json
 * @see https://yuque.antfin-inc.com/mo/spec/spec-materials#3BIaD
 */
export interface MaterialPackageJSON {
  name: string;
  /** 版本 */
  version: string;
  /** 入口 */
  main: string;
  /** 业务组件描述 */
  description: string;
  /** 【私有字段】组件配置信息 */
  componentConfig?: {
    /** 组件英文名 */
    name: string;
    /** 组件中文名 */
    title: string;
    /** 组件分类 */
    category: string;
  };
  /** 【私有字段】区块配置信息 */
  blockConfig?: {
    /** 区块英文名 */
    name: string;
    /** 区块中文名 */
    title: string;
    /** 区块分类 */
    category: string;
  };
}

export interface WhaleParseOptions {
  /**
   * 指定默认导出组件的名字
   */
  defaultComponentName?: string;
  ingoreFileNames?: string[];
  /**
   * 下钻深度
   * @default 2
   */
  typeLevel?: number;
}
