import { parse, WmParserOptions } from 'whale-docgen-ts';

export const defaultParserOptions: WmParserOptions = {
  savePropValueAsString: true,
};

export const parseTSComponent = (
  filePathOrPaths: string | string[],
  parserOpts?: WmParserOptions
) =>
  parse(filePathOrPaths, {
    ...defaultParserOptions,
    ...parserOpts,
  });

export const isExportDefault = (exportName: string) =>
  [
    'default',
    '__function',
    'Stateless',
    'StyledComponentClass',
    'StyledComponent',
    'FunctionComponent',
    'StatelessComponent',
    'ForwardRefExoticComponent',
  ].includes(exportName);
