import * as gitRemoteOriginUrl from 'remote-origin-url';
import * as urlFromGit from 'github-url-from-git';

export const getGitRemoteUrl = (cwd?: string) =>
  urlFromGit(gitRemoteOriginUrl.sync(cwd), {
    extraBaseUrls: ['gitlab.alibaba-inc.com'],
  }) || 'https://gitlab.alibaba-inc.com/whale/mobile-materials/';
