import * as React from 'react';

function withContainer<Props>(C: React.ComponentType<Props>): React.FC<Props> {
  return (props, ref) => (
    <div ref={ref}>
      <C {...props} />
    </div>
  );
}

interface SelectProps {
  a: string;
}

const SelectFC: React.FC<SelectProps> = () => <div />;

class SelectC extends React.Component<SelectProps> {
  render() {
    return <div />;
  }
}

export const SelectFC2 = withContainer(SelectFC);
export const SelectC2 = withContainer(SelectC);
