import {
  WmPropItemType,
  WmUnionPropItemType,
  WmPlainPropItemType,
  WmIntersectionPropItemType,
  WmObjectPropItemType,
  WmArrayPropItemType,
} from '../types';

export const isWmPlainPropItemType = (
  type: WmPropItemType
): type is WmPlainPropItemType => type.name === 'plain';
export const isWmUnionPropItemType = (
  type: WmPropItemType
): type is WmUnionPropItemType => type.name === 'union';
export const isWmIntersectionPropItemType = (
  type: WmPropItemType
): type is WmIntersectionPropItemType => type.name === 'intersection';
export const isWmObjectPropItemType = (
  type: WmPropItemType
): type is WmObjectPropItemType => type.name === 'object';

export const isWmArrayPropItemType = (
  type: WmPropItemType
): type is WmArrayPropItemType => type.name === 'array';

export const createWmPlainPropItemType = (
  raw: string
): WmPlainPropItemType => ({
  name: 'plain',
  raw,
});
